mapper.select:
select id,classId,goodsName,zoneType,isSuit,price,title,specification,hostImg,details,createTime
from p_goods

  isDel = 0

    and classId=#{classId}


    and zoneType=#{zoneType}


    and isSuit=#{isSuit}


order by sort asc,createTime desc

mapper.update:
update p_goods
set repertory = #{repertory},
  soldNum = #{soldNum}
where id = #{id}
and repertory=#{oldRepertory}

mapper.select:
select g.id,g.classId,g.goodsName,g.zoneType,g.isSuit,g.price,g.title,g.specification,g.hostImg,g.details,g.isDel,g.createTime,g.sort,g.repertory,g.soldNum,g.sort, c.className from p_goods g left join p_goods_class c on g.classId=c.id
and g.goodsName like concat(concat('%', #{goodsName}), '%')
and g.classId = #{classId}
and g.zoneType = #{zoneType}
order by g.sort asc,g.createTime desc

select

from p_goods
where goodsName = #{goodsName}
and zoneType = #{zoneType}
limit 1
