mapper.select:
select m.* from t_menu m left join t_role_menu p on m.id = p.mid


    and p.rid=#{rid}


    and m.id=#{id}


    and m.mid=#{mid}

  and m.menuType!="other"

group by m.id,m.mid,m.menuUrl,m.menuName,m.menuType
order by m.id

SELECT m.* FROM t_menu m left join t_role_menu p on m.id=p.mid


    and p.rid=#{rid}


    and m.id=#{id}


    and m.mid=#{mid}


group by m.id,m.mid,m.menuUrl,m.menuName,m.menuType
order by m.id

SELECT count(*) FROM t_menu m


    and m.id=#{id}


    and m.mid=#{mid}

select m.* from t_menu m left join t_role_menu p on m.id = p.mid where
p.rid=#{rid}

  and m.id=#{id}


  and m.pid=#{pid}


  and m.menuApiPath =  #{url}

limit 1
