mapper.select:
select ifnull(sum(realPrice),0.00)
from p_order
where uid=#{uid}
and isSuit=0
and id=#{id}
 and state >= 1

select o.*
from p_order o
left join p_user u on o.uid=u.id
where u.strawUid=#{strawUid}
and o.isSuit=1
and o.zoneType=0
and o.realPrice > 0
 and o.state >= 1 
 and o.payTime >= #{startTime}
 and o.payTime <= #{endTime}

select o.*,u.phone userPhone
from p_order o
left join p_user u on o.uid=u.id


    and u.phone like concat(concat('%', #{userPhone}), '%')


    and o.payType = #{payType}


    and o.uid = #{uid}


    and o.state = #{state}


     and o.createTime >= #{startTime}


     and o.createTime <= #{endTime}

select *
from p_order
where uid=#{uid}
and isDel = 0

  and state = #{state}

order by createTime desc

select count(*) from p_order
where uid=#{uid}
and zoneType=#{zoneType}
  and state >= 1
